function dz = Sat_Dynamics_Voltra_IC(t,z)
global m_sat m Ixx Iyy Izz Ixy Ixz Iyz l a tau1 tau2 tau3 px py pz
F = -0.018*sin(0.089*t) + 0.012*cos(0.0485*t);
q1 = z(1); q2 = z(2); q3 = z(3); q4 = z(4);
U1 = z(8); U2 = z(9); U3 = z(10); U4 = z(11);
M = MassMat(Ixx,Ixy,Ixz,Iyy,Iyz,Izz,a,l,m,m_sat,q4);
B = Bias(F,Ixx,Ixy,Ixz,Iyy,Iyz,Izz,U1,U2,U3,U4,a,l,m,m_sat,q4,tau1,tau2,tau3);
dq = dq_itu_Voltra(U1,U2,U3,U4,a,l,m,m_sat,px,py,pz,q1,q2,q3,q4);
POW = Power(F,U1,U2,U3,U4,q2,q3,tau1,tau2,tau3);
dz = [dq; -inv(M)*B; POW];                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
